INSERT INTO table VALUES(25,'Gradient',331,15,NULL,'/TopoMetrics/Metrics/Thalweg/WSGradientPC',0.0500000007450581,0.0,5.0,1,'Grad',NULL,10023,20,1,31,35,'Site water surface gradient is calculated as the difference between the top of site (upstream) and bottom of site (downstream) water surface elevations divided by thalweg length.',NULL,'https://github.com/SouthForkResearch/CHaMP_Metrics/wiki/GradientandSinuosity',2011,'1','1','The start and end points of the thalweg are used to extract elevations from the water surface DEM. Generated by the River Bathymetry Toolkit (RBT).','rbt_results/metric_results/site_water_surface_gradient','2017-05-23 16:49:59','2017-05-23 23:03:39');
INSERT INTO table VALUES(26,'Sinuosity',2,15,NULL,'/TopoMetrics/Metrics/Thalweg/Sinuosity',0.0500000007450581,1.0,2.3,1,'Sin',NULL,10023,30,1,31,35,'Ratio of the thalweg length (Site Length Thalweg) to the straight line distance between the start and end points of the thalweg. Generated by the River Bathymetry Toolkit (RBT).','https://www.monitoringresources.org/Document/Method/Details/1265','https://github.com/SouthForkResearch/CHaMP_Metrics/wiki/GradientandSinuosity',2011,'1','1','(Site Length Thalweg)/(straight line distance between the start and end points of thalweg)','rbt_results/metric_results/site_sinuosity','2017-05-23 16:49:59','2017-05-26 23:05:45');
